using System; using System.Collections; using System.Collections.Generic; using UnityEngine;  public class RooAi {     private Board board;     private Room source;     private Room dest;      public RooAi(Board board)     {         this.board = board;     }      public void Decide()     {         MakeMonkeyMove();     }      public void MakeMonkeyMove()     {         source = PickMonkeyBead();         // Picking a destination room         int[] validDsts = source.GetValidMoves();         if(validDsts.Length == 0) // No possible move
        {
            MakeMonkeyMove(); // Start again with a new source
        }         dest = board.rooms[validDsts[UnityEngine.Random.Range(0, validDsts.Length)]];     }      public Room PickMonkeyBead()     {         return board.rooms[board.GetBeadPositions(Room.Bead.ROO)[(int)UnityEngine.Random.Range(0, 3)]];     }          public Room getSource()     {         return this.source;     }      public Room getDest()     {         return this.dest;     }  } 